    function GetJackpotView(jpType) {
        $.ajax({
            type: "GET",
            data: { jt: jpType },
            url: "/Home/GetJackpotView",
            success: function (result) {
                $('#jackpot_container_').html(dlAnimate(result));
            }
        });
    }

    function SetJackpot(type) {
        switch (type) {
            case '1':
                if (typeof PrgJackpots != 'undefined') {
                    let prgNodes = document.querySelectorAll('#jackpot_container_ .js_prg_jack_item');
                    if (prgNodes.length > 0) {
                        PrgJackpots.destroy(prgNodes);
                    }
                }
                GetJackpotView(type);
                break;
            case '3':
                if (typeof Jackpots != 'undefined') {
                    let nodes = document.querySelectorAll('#jackpot_container_ .js_jack_item');
                    if (nodes.length > 0) {
                        Jackpots.destroy(nodes);
                    }
                }
                GetJackpotView(type);
                break;
        }
        allowChange = true;
    }

    $(document).ready(function () {
        let firstJack = document.querySelector('.js_jackpot_types');
        if (firstJack) {
            SetJackpot(firstJack.dataset.jpType);
        }
    });
</script>

